import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AboutPhoto from './images/About.jpg';
import "./EducationSection.css";
import AOS from 'aos';
import 'aos/dist/aos.css';

function EducationSection() {
    const [selectedYear, setSelectedYear] = useState(2023);
  
    const handleYearClick = (year) => {
      setSelectedYear(year);
    };
  
    return (
        <div class="education-section" id='education'>
            <div className="about-heading" data-aos="zoom-in">
                <h2>02. Education</h2>
            </div>
            <br></br>
        <div class="tabs">
            <button class="tab" data-year="2023">2020</button>
            <button class="tab" data-year="2023">2021</button>
            <button class="tab" data-year="2022">2022</button>
            <button class="tab" data-year="2021">2023</button>
        </div>
        <div class="education-content">
          <div class="year-content" data-year="2023">
            <p>Information about your education in 2023</p>
          </div>
          <div class="year-content" data-year="2022">
            <h3>Education in 2022</h3>
            <p>Information about your education in 2022</p>
          </div>
          <div class="year-content" data-year="2021">
            <h3>Education in 2021</h3>
            <p>Information about your education in 2021</p>
          </div>
        </div>
      </div>
    );
  }
  
  export default EducationSection;
