import React, { useState, useEffect } from 'react';
import Cursor from './Cursor.js';
import { motion } from 'framer-motion';
import myCV from './CV/GeorgePhillipsCV.pdf';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';;


function HeroSection() {

    function openResume() {
        window.open(myCV);
    }

    const [isHovering, setIsHovering] = useState(false);

    function handleTextEnter() {
        setIsHovering(true);
    }

    function handleTextLeave() {
        setIsHovering(false);
    }

    return (
        <div className='hero-container'>
            <Cursor textEnter={handleTextEnter} textLeave={handleTextLeave}></Cursor>

            <div class="vertical-line-left"></div>
            <div class="vertical-line-right"></div>
            <p>Hi, my name is</p>
            <h1 className='App-name' onMouseEnter={textEnter} onMouseLeave={textLeave}>
                {isHovering ? <span style={{ color: '#fff' }}>George Phillips</span> : 'George Phillips'}
            </h1>
            <h2>I am a Software Developer</h2>
            <div className='hero-btns'>
                <Button
                    className='btns'
                    buttonStyle='btn--outline'
                    buttonSize='btn--large'
                    onClick={openResume}
                >
                    Resume
                </Button>
            </div>
        </div>
    );
}

export default HeroSection;
