import React from "react";
import { Sphere, MeshDistortMaterial, GradientTexture } from "@react-three/drei";
import * as THREE from "three"; // add this line to import THREE

export default function AnimatedSphere() {
  // Define the gradient colors
  const gradientColors = ["#5F4BB6", "#F74269", "#FFE484"];

  // Create the gradient texture
  const gradientTexture = new GradientTexture();
  gradientTexture.colors = gradientColors.map((color) =>
    new THREE.Color(color)
  );

  return (
    <Sphere visible args={[1, 100, 200]} scale={2}>
      <MeshDistortMaterial
        attach="material"
        distort={0.5}
        speed={2}
        roughness={1}
        gradientMap={gradientTexture}
      />
    </Sphere>
  );
}
