import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import AboutPhoto from './images/About.jpg';
import "./EducationSection.css";
import AOS from 'aos';
import 'aos/dist/aos.css';

const EducationSection = () => {
    const [selectedYear, setSelectedYear] = useState(null);
  
    const handleYearClick = (year) => {
      setSelectedYear(year);
    };
  
    const years = [
      { year: 2020, content: "Information about education in 2020" },
      { year: 2021, content: "Information about education in 2021" },
      { year: 2022, content: "Information about education in 2022" },
      { year: 2023, content: "Information about education in 2023" },
    ];
  
    return (
      <div>
        <h2>Education</h2>
        <div className="education-years">
          {years.map((yearObj) => (
            <div
              key={yearObj.year}
              className={`education-year ${
                selectedYear === yearObj.year ? "active" : ""
              }`}
              onClick={() => handleYearClick(yearObj.year)}
            >
              {yearObj.year}
            </div>
          ))}
        </div>
        <div className="education-content">
          {selectedYear ? (
            <p>{years.find((yearObj) => yearObj.year === selectedYear).content}</p>
          ) : (
            <p>Please select a year to see education information</p>
          )}
        </div>
      </div>
    );
  };
  
  export default EducationSection;
