import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import AboutPhoto from './images/About.jpg';
import "./AboutSection.css";
import AOS from 'aos';
import 'aos/dist/aos.css';

function AboutSection () {

    {/* Scroll Animation */ }
    useEffect(() => {
        AOS.init({ duration: 2000 });
    })
    
    return (
        <section className="about-section" >
            <div className="about-heading" data-aos="zoom-in">
                <h2>01. About</h2>
            </div>
            <div className="about-content" data-aos="zoom-in">
                <img src={AboutPhoto} alt="Your Photo" className="about-photo" data-aos="zoom-in" />

                <p className="about-text">Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam lobortis turpis est, vitae pulvinar mauris consequat ut.
                 Donec ut dolor non ante eleifend varius eu at elit. Donec lacinia dui ut dolor fringilla, 
                 ac laoreet sapien efficitur. Nulla vestibulum velit at justo finibus, quis varius lectus facilisis. 
                 Etiam vel tempor odio, nec venenatis mi. Ut non mi vel eros pulvinar placerat. Nulla facilisi. 
                 In eu odio et enim consectetur faucibus. Vivamus nec lorem vel ipsum congue vehicula. Fusce a purus eget diam fermentum accumsan. 
                 Nam ut erat vel risus finibus egestas.
                 </p>

            </div>
        </section>
    )
}

export default AboutSection;
