import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AboutPhoto from './images/About.jpg';
import "./EducationSection.css";
import AOS from 'aos';
import 'aos/dist/aos.css';

function EducationSection() {
    const [selectedYear, setSelectedYear] = useState(2023);
  
    const handleYearClick = (year) => {
      setSelectedYear(year);
    };
  
    return (
      <div className="education-section" id='education'>
        <div className="tab-row">
          <div
            className={`tab ${selectedYear === 2020 ? "active" : ""}`}
            onClick={() => handleYearClick(2020)}
          >
            2020
          </div>
          <div
            className={`tab ${selectedYear === 2021 ? "active" : ""}`}
            onClick={() => handleYearClick(2021)}
          >
            2021
          </div>
          <div
            className={`tab ${selectedYear === 2022 ? "active" : ""}`}
            onClick={() => handleYearClick(2022)}
          >
            2022
          </div>
          <div
            className={`tab ${selectedYear === 2023 ? "active" : ""}`}
            onClick={() => handleYearClick(2023)}
          >
            2023
          </div>
        </div>
        <div className="education-info">
          {selectedYear === 2020 && <div>Education info for 2020</div>}
          {selectedYear === 2021 && <div>Education info for 2021</div>}
          {selectedYear === 2022 && <div>Education info for 2022</div>}
          {selectedYear === 2023 && <div>Education info for 2023</div>}
        </div>
      </div>
    );
  }
  
  export default EducationSection;
