import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import AboutPhoto from './images/About.jpg';
import "./EducationSection.css";
import AOS from 'aos';
import 'aos/dist/aos.css';

const EducationSection = () => {
    const [selectedYear, setSelectedYear] = useState(null);
  
    const handleYearClick = (year) => {
      if (selectedYear === year) {
        setSelectedYear(null);
      } else {
        setSelectedYear(year);
      }
    };
  
    return (
      <div className="education-container">
        <h2>Education</h2>
        <div className="education-years">
          <div
            className={`education-year ${
              selectedYear === "2023" ? "selected" : ""
            }`}
            onClick={() => handleYearClick("2023")}
          >
            <h3>2023</h3>
            {selectedYear === "2023" && (
              <p>
                I am currently pursuing a Bachelor's degree in Computer Science
                from XYZ University.
              </p>
            )}
          </div>
          <div
            className={`education-year ${
              selectedYear === "2022" ? "selected" : ""
            }`}
            onClick={() => handleYearClick("2022")}
          >
            <h3>2022</h3>
            {selectedYear === "2022" && (
              <p>
                Completed my senior secondary education with a focus on science
                stream from ABC School.
              </p>
            )}
          </div>
          <div
            className={`education-year ${
              selectedYear === "2021" ? "selected" : ""
            }`}
            onClick={() => handleYearClick("2021")}
          >
            <h3>2021</h3>
            {selectedYear === "2021" && (
              <p>
                Completed my high school education with a focus on science stream
                from XYZ School.
              </p>
            )}
          </div>
          <div
            className={`education-year ${
              selectedYear === "2020" ? "selected" : ""
            }`}
            onClick={() => handleYearClick("2020")}
          >
            <h3>2020</h3>
            {selectedYear === "2020" && (
              <p>
                Started my education journey with a focus on science stream from
                ABC School.
              </p>
            )}
          </div>
        </div>
      </div>
    );
  };
  
  export default EducationSection;
